/* AutoDeals Main JS - Minified */
document.addEventListener('DOMContentLoaded',function(){const menuToggle=document.querySelector('.menu-toggle');const navList=document.querySelector('nav ul');if(menuToggle&&navList){menuToggle.addEventListener('click',function(){navList.classList.toggle('active');menuToggle.setAttribute('aria-expanded',navList.classList.contains('active'))})}const navLinks=document.querySelectorAll('nav a');navLinks.forEach(link=>{link.addEventListener('click',function(){if(window.innerWidth<=768){navList.classList.remove('active');menuToggle.setAttribute('aria-expanded','false')}})});const currentPage=window.location.pathname.split('/').pop()||'index.html';navLinks.forEach(link=>{const href=link.getAttribute('href');if(href&&(href===currentPage||href.includes(currentPage))){link.classList.add('active')}});window.addEventListener('resize',function(){if(window.innerWidth>768){navList.classList.remove('active');menuToggle.setAttribute('aria-expanded','false')}})});