/* AutoDeals Theme Switcher - Minified */
document.addEventListener('DOMContentLoaded',function(){const themeSelect=document.getElementById('theme-select');const themeLink=document.getElementById('theme-link');const logoText=document.querySelector('.logo-text');function applyTheme(theme){const themeMap={'default':'../css/theme-default.css','dark':'../css/theme-dark.css','light':'../css/theme-light.css'};const logoColors={'default':'#3498db','dark':'#f39c12','light':'#27ae60'};if(themeMap[theme]){themeLink.href=themeMap[theme];if(logoText){logoText.style.color=logoColors[theme]}localStorage.setItem('selectedTheme',theme)}}const savedTheme=localStorage.getItem('selectedTheme')||'default';themeSelect.value=savedTheme;applyTheme(savedTheme);themeSelect.addEventListener('change',function(){applyTheme(this.value)})});